import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function DogDetails(props) {
    const { name } = useParams()
    const [dog, setDog] = useState()
    props.data.filter(dog => {
        console.log(dog)
        if (dog.name.toLowerCase() == name) {
            setDog({
                name: dog.name,
                age: dog.age,
                src: dog.src,
                facts: dog.facts
            })
        }
    })

    useEffect(() => {
        props.data.filter(dog => {
            console.log(dog)
            if (dog.name.toLowerCase() == name) {
                setDog({
                    name: dog.name,
                    age: dog.age,
                    src: dog.src,
                    facts: dog.facts
                })
            }
        })

    }, dog)

    console.log(dog)


    return (
        <div className="DogDetails">
            <h2>Hello! My name is {dog.name} and I'm {dog.age} years old!</h2>
            <p>Here are some facts about me :D</p>
            <ul>
                {dog.facts.map(fact => {
                    return <li>{fact}</li>
                })}
            </ul>
        </div>
    )
}

export default DogDetails;
