import { useParams } from "react-router-dom";
import axios from "axios";

function DogDetails(props) {
  const { name } = useParams();
  const [content, setContent] = useState()
  const dog = props.data.filter((dog) => {
    if (dog.name.toLowerCase() == name.toLowerCase()) {
      setContent(
        <div className="DogDetails">
          <h2>
            Hello! My name is {dog.name} and I'm {dog.age} years old!
          </h2>
          <img src={dogsrc} style={{ height: "300px", width: "300px" }} />
          <p>Here are some facts about me :D</p>
          <ul>
            {dog.facts.map((fact) => {
              return <li>{fact}</li>;
            })}
          </ul>
        </div>
      )
    } else {
        setContent(<h1>Sorry, we don't have a dog with that name!</h1>)
    }
  });

  console.log(dog[0].facts);

  return (
    <div className="DogDetails">
      <h2>
        Hello! My name is {dog[0].name} and I'm {dog[0].age} years old!
      </h2>
      <img src={dog[0].src} style={{ height: "300px", width: "300px" }} />
      <p>Here are some facts about me :D</p>
      <ul>
        {dog[0].facts.map((fact) => {
          return <li>{fact}</li>;
        })}
      </ul>
    </div>
  );
}

export default DogDetails;
