import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function DogDetails(props) {
    const { name } = useParams()
    const dog = props.data.filter(dog => {
        if (dog.name.toLowerCase() == name) return dog 
    })
    const [theDog, setTheDog] = useState(dog)

    useEffect(() => {
         props.data.filter(dog => {
            if (dog.name.toLowerCase() == name) {
                setTheDog({
                    name: dog.name,
                    age: dog.age,
                    src: dog.src,
                    facts: dog.facts
                })
            }
        })

    }, theDog)

    console.log(dog[0].facts)

    return (
        <div className="DogDetails">
            <h2>Hello! My name is {dog[0].name} and I'm {dog[0].age} years old!</h2>
            <p>Here are some facts about me :D</p>
            <ul>
                {dog[0].facts.map(fact => {
                    return <li>{fact}</li>
                })}
            </ul>
        </div>
    )
}

export default DogDetails;
